import React from "react";
import "../Styles/SpecialPackages.css";

const Package = ({ name, price, features }) => {
	return (
		<div className='package'>
			<h3>{name}</h3>
			<h4>${price}</h4>
			<ul>
				{features.map((feature, index) => (
					<li key={index}>{feature}</li>
				))}
			</ul>
		</div>
	);
};

const App = () => {
	const packages = [
		{
			name: "Budget Explorer",
			price: 1000,
			features: [
				"Basic hotel accommodations",
				"Public transportation",
				"Self-guided tours",
				"Local food experiences",
			],
		},
		{
			name: "Cultural Immersion",
			price: 2500,
			features: [
				"Mid-range hotel accommodations",
				"Guided tours to cultural and historical sites",
				"Local cuisine and cooking classes",
				"Authentic cultural experiences",
			],
		},
		{
			name: "Luxury Explorer",
			price: 5000,
			features: [
				"Luxury hotel accommodations",
				"Private transportation",
				"Private guided tours",
				"Fine dining and wine experiences",
			],
		},
	];

	return (
		<div className='container'>
			<h1>Tour Packages</h1>
			<div className='packages'>
				{packages.map((tourPackage, index) => (
					<Package
						key={index}
						name={tourPackage.name}
						price={tourPackage.price}
						features={tourPackage.features}
					/>
				))}
			</div>
		</div>
	);
};

export default Package;
