import  React, {Component} from 'react';
import axios from 'axios';
import {BrowserRouter as Router, Link} from "react-router-dom";
import {Button, Form, FormControl, Navbar} from "react-bootstrap";

import logo from "../logo.png";
import './css/LandingPage.css';
import Footer from './footer'


export default  class Create extends  Component{


    constructor(props) {
        super(props);
        this.onChangeName = this.onChangeName.bind(this);
        this.onChangeAddress = this.onChangeAddress.bind(this);
        this.onChangeNIC = this.onChangeNIC.bind(this);
        this.onChangePhone = this.onChangePhone.bind(this);
        this.onChangeEmail = this.onChangeEmail.bind(this);
        this.onChangePassword = this.onChangePassword.bind(this);
        this.onChangecPassword = this.onChangecPassword.bind(this);
        this.onSubmit = this.onSubmit.bind(this);

        this.state = {
            name: '',
            address: '',
            nic:'',
            phone:'',
            email:'',
            password:'',
            cpassword:''
        }
    }
    onChangeName(e){
        this.setState( {
           name: e.target.value
        });
    }
    onChangeAddress(e){
        this.setState( {
            address: e.target.value
        });
    }
    onChangeNIC(e){
        this.setState( {
            nic: e.target.value
        });
    }
    onChangePhone(e){
        this.setState( {
            phone: e.target.value
        });
    }
    onChangeEmail(e){

            this.setState({
                email: e.target.value
            });
    }
    onChangePassword(e){
        this.setState( {
            password: e.target.value
        });
    }
    onChangecPassword(e){
        this.setState( {
            cpassword: e.target.value
        });
    }
    onSubmit(e){
        e.preventDefault();
        const obj = {
            name : this.state.name,
            address : this.state.address,
            nic : this.state.nic,
            phone : this.state.phone,
            email : this.state.email,
            password : this.state.password
        };
        if(this.state.customer_type === "Buyer" || this.state.customer_type==="Seller"){
            if(this.state.phone.MAX_LENGTH === 10){
                axios.post('http://localhost:4000/business/add',obj)
                    .then(res => {
                        alert("Registration Successfully")
                        console.log(res.data)});
                this.props.history.push('/signIn');
            }
            else{
                alert('Invalid phone number')
            }

        }else{
            alert('User type invalid.. Please enter "Buyer" or "Seller" ')
        }


        this.setState({
            name :'',
            address :'',
            nic :'',
            phone:'',
            email:'',
            password:''

        })

    }

    render() {
        return(
            <div class = "wrap">
                    <Navbar>
                        <Navbar.Brand href="#home">
                            <img
                                alt=""
                                src={logo}
                                width="200"
                                height="120"

                            />{''}
                            <h2 className="d-xl-inline">University of Information Technology</h2>
                        </Navbar.Brand>
                            <img src = "https://img.freepik.com/free-vector/flat-design-minimalistic-technology-twitch-banner_23-2149107142.jpg" height="100"/>
                            <img src = "https://img.freepik.com/free-vector/gradient-halftone-technology-twitch-banner_23-2149164513.jpg?w=360" height="100"/>
                    </Navbar>
                    <nav className="navbar navbar-expand-lg navbar-light bg-info">
                        <div className="collapse navbar-collapse" id = "navbarSupportedContent">
                            <ul className="navbar-nav mr-auto font-weight-bold form-control-lg text-dark ">
                                <li className="nav-item">
                                    <Link to={'/'} className = "nav-link">Home</Link>
                                </li>
                                <li className="nav-item">
                                    <Link to={'/create'} className = "nav-link ">New Student</Link>
                                </li>

                                <li className="nav-item">
                                    <Link to={'/aboutUs'} className = "nav-link">About Us</Link>
                                </li>
                                <li className="nav-item">
                                    <Link to={'/aboutUs'} className = "nav-link">Contact Us</Link>
                                </li>
                                <li className="nav-item">
                                    <Link to={'/signIn'} className = "nav-link">Sign In</Link>
                                </li>
                            </ul>
                        </div>
                        <Form inline>
