import  React, {Component} from 'react';
import axios from 'axios';
import {BrowserRouter as Router, Link} from "react-router-dom";
import {Button, Form, FormControl, Navbar} from "react-bootstrap";

import logo from "../logo.png";
import './css/LandingPage.css';
import Footer from './footer';


export default  class Create extends  Component{


    constructor(props) {
        super(props);
        this.onChangepName = this.onChangepName.bind(this);
        this.onChangestock = this.onChangestock.bind(this);
        this.onChangepCode = this.onChangepCode.bind(this);
        this.onChangecost = this.onChangecost.bind(this);
        this.onChangevender = this.onChangevender.bind(this);

        this.onSubmit = this.onSubmit.bind(this);

        this.state = {
            pName: '',
            stock: '',
            pCode:'',
            cost:'',
            vender:''


        }
    }
    onChangepName(e){
        this.setState( {
            pName: e.target.value
        });
    }
    onChangestock(e){
        this.setState( {
            stock: e.target.value
        });
    }
    onChangepCode(e){
        this.setState( {
            pName: e.target.value,
            pCode:"IN"+ e.target.value.substring(0, 6)

        });
    }
    onChangecost(e){
        this.setState( {
            cost: e.target.value
        });
    }
    onChangevender(e){
        this.setState( {
            vender: e.target.value
        });
    }

    onSubmit(e){
        e.preventDefault();
        const obj = {
            pName : this.state.pName,
            stock : this.state.stock,
            pCode : this.state.pCode,
            cost : this.state.cost,
            vender : this.state.vender,

        };



        alert("Your Product code is Id is - " +this.state.pCode);

            if(this.state.pName.length >= 1){
                if(this.state.stock.length > 0){
                    if(this.state.cost.length > 1){

                            axios.post('http://localhost:4000/inventory/add',obj)
                                .then(res => {
                                    alert("Registration Successfully");
                                    this.setState({
                                        pName: '',
                                        stock: '',
                                        pCode:'',
                                        cost:'',
                                        vender:''

                                    })
                                    console.log(res.data)});
                            this.props.history.push('/signIn');

                    } 
                    else {
                        alert('Invalid Cost Number.. Pleace enter more than 1 digit.');
                    }
                }
                else{
                    alert('Invalid Stock number.. Pleace enter more than 1 digits.');
                }
            } 
            else {
                alert('Pleace enter at more than 1 caractors');
            }

    }




    render() {
        return(
            <div class = "wrap">
                    <Navbar>
                      </Navbar>

                      <nav className="navbar navbar-expand-lg navbar-light bg-info ">
                        <div className="collapse navbar-collapse" id = "navbarSupportedContent">
                            <ul className="navbar-nav mr-auto font-weight-bold form-control-lg text-dark ">

                                <li className="nav-item">
                                    <Link to={'/'} className = "nav-link">Home</Link>
                                </li>
                                <li className="nav-item">
                                    <Link to={'/create'} className = "nav-link ">Add Inventory</Link>
                                </li>

                                <li className="nav-item">
                                    <Link to={'/aboutUs'} className = "nav-link">About Us</Link>
                                </li>
                                <li className="nav-item">
                                    <Link to={'/aboutUs'} className = "nav-link">Contact Us</Link>
                                </li>
                                <li className="nav-item">
                                    <Link to={'/aboutUs'} className = "nav-link"></Link>
                                </li>
                                <li className="nav-item">
                                    <Link to={'/signIn'} className = "nav-link">Sign In</Link>
                                </li>

                            </ul>
                        </div>
                        <Form inline >
                        <div class="form-group">
                            <input class="form-control input-sm mr-1"  type="text" placeholder='search here....'/>
                            <Button type="submit" className='btn btn-info btn-sm'>search</Button>
                        </div>

                        </Form>
                    </nav>
                    <br/>
                    <div className='row-frm'>
                        <div className='col-frm-1'>
                            <img src = "https://www.scnsoft.com/software-development-services/creating-inventory-management-system/creating-an-inventory-system_cover-upd.svg" width = "" height = ""/>

                        </div>
                        <div className="container reg-frm" style={{marginTop:10}}>
                            <h3 className="text-center">Add Inventory</h3>
                            <form onSubmit={this.onSubmit}>
                                <div className="form-group">
                                    <label>Product Name :</label>
                                    <input type ="text" required placeholder = "Please enter product  name" className="form-control" value={this.state.name} onChange = {this.onChangeName}/>
                                </div>

                                <div className="form-group">
                                    <label>Stock :</label>
                                    <input type ="text" required placeholder = "Please enter stock" className="form-control" value={this.state.nic} onChange = {this.onChangeNIC}/>
                                </div>
                                <div className="form-group">
                                    <label style={{color:'Black',fontWeight:'bold'}}>Product code :</label>
                                    <input type ="text"  className="form-control" value={this.state.campusid} readOnly style={{color:'green',fontWeight:'bold'}}/>
                                </div>
                                <div className="form-group">
                                    <label >Cost :</label>
                                    <input type ="text" required placeholder = "Please enter cost" className="form-control" value={this.state.phone} onChange = {this.onChangePhone}/>
                                </div>

                                <div className="form-group">
                                    <label>Vender :</label>
                                    <input type ="text" required placeholder = "Please enter product  name" className="form-control" value={this.state.name} onChange = {this.onChangeName}/>
                                </div>

                                <div className="form-group">
                                    <input type = "submit" value = "Register" className="btn btn-info"/>
                                </div>

                                <br/>

                            </form>
                        </div>
                    </div>

                    <br/><br/>

                    <div>
                         <hr className="shadow-lg card-footer"/>
                    </div>
                    <Footer/>
            </div>
        )
    }
}
