import  React, {Component} from 'react';
import axios from 'axios'
import TableRow from './TableRow';
import {Button, Form, FormControl, Navbar} from "react-bootstrap";
import {BrowserRouter as Router, Link} from "react-router-dom";

import logo from '../logo.png';
import Footer from './footer'
import './css/LandingPage.css';

export default  class Index extends  Component{


    constructor(props) {
        super(props);
        this.state = {student : []};
        this.state.Email = this.props.match.params.id;

        //const Email = this.props.match.params.id;
    }

    componentDidMount() {
        alert('email is ' +this.props.match.params.id);
        axios.get('http://localhost:4000/campus/'+this.props.match.params.id)
            .then(response => {
                alert('Pass una')
                alert('Data Tika :'+response.data)
                this.setState({student : response.data});

            })
            .catch(function (error){
                console.log(error);
            })
    }
    tabRow(){
            return <TableRow obj={this.state.student}/>
    }

    render() {
        return(
                <div className='wrap'>
                    <Navbar>
                        <Navbar.Brand href="#home">
                            <img
                                alt=""
                                src={logo}
                                width="20"
                                height="120"

                            />{''}
                            <h1 className="d-xl-inline">Online Buy and Sell Shopping Store</h1>
                        </Navbar.Brand>
                    </Navbar>
                    <nav className="navbar navbar-expand-lg navbar-light bg-info">
                        <div className="collapse navbar-collapse" id = "navbarSupportedContent">
                            <ul className="navbar-nav mr-auto font-weight-bold form-control-lg text-dark ">
                                <li className="nav-item">
                                    <Link to={'/BuyerDash/'+this.state.Email} className = "nav-link">Home</Link>
                                </li>
                                <li className="nav-item">
                                    <Link to={'/index/:id'} className = "nav-link">Profile</Link>
                                </li>
                                <li className="nav-item">
                                    <Link to={'/logout'} className = "nav-link ">LogOut</Link>
                                </li>

                            </ul>
                        </div>
                        <Form inline>
                            <div class="form-group">
                                <input class="form-control input-sm mr-1"  type="text" placeholder='search here....'/>
                                <Button type="submit" className='btn btn-info btn-sm'>search</Button>
                            </div>
                        </Form>
                    </nav>
                    <br/>

                <h3 align="center">My Profile</h3>
                <table className="table table-striped" style = {{marginTop :20}}>
                    <thead>
                    <tr>
                        <th>ID</th>
                        <th>Name</th>
                        <th>Address</th>
                        <th>NIC</th>
                        <th>Phone Number</th>
                        <th>eMail</th>
                        <th>Password</th>
                        <th colSpan="2">Action</th>
                    </tr>
                    </thead>
                    <tbody>
                    {this.tabRow()}
                    </tbody>
                </table>

                <div>
                    <hr className="shadow-lg card-footer"/>
                </div>
                <Footer/>
             </div>
        );
    }
}
