import React, { Component } from "react";
import "../Styles/EditProducts.css";
import draganddropimage from "../images/drag-and-drop-image.svg";
import Header from "./NavBar";
import axios from "axios";

import logo from "../images/navbar-logo.svg";

import "../Styles/LeftSidebar.css";

import img1 from "../images/dashboard-icon.svg";
import img2 from "../images/farmer-icon.svg";
import img3 from "../images/vender-icon.svg";
import img4 from "../images/products-icon.svg";
import img5 from "../images/clients-icon.svg";

export default class EditProducts extends Component {
	constructor(props) {
		super(props);
		this.onChangepName = this.onChangepName.bind(this);
		this.onChangesLocation = this.onChangesLocation.bind(this);
		this.onChangepID = this.onChangepID.bind(this);
		this.onChangecustomers = this.onChangecustomers.bind(this);
		this.onChangeimage = this.onChangeimage.bind(this);

		this.onSubmit = this.onSubmit.bind(this);

		this.state = {
			vName: "",
			lName: "",
			dob: "",
			pNumber: "",
			uName: "",
			password: "",
			email: "",
			image: "",
		};
	}
	onChangevName(e) {
		this.setState({
			vName: e.target.value,
		});
	}
	onChangelName(e) {
		this.setState({
			lName: e.target.value,
		});
	}
	onChangedob(e) {
		this.setState({
			dob: e.target.value,
		});
	}
	onChangepNumber(e) {
		this.setState({
			pNumber: e.target.value,
		});
	}
	onChangeuName(e) {
		this.setState({
			uName: e.target.value,
		});
	}
	onChangepassword(e) {
		this.setState({
			password: e.target.value,
		});
	}
	onChangeemail(e) {
		this.setState({
			email: e.target.value,
		});
	}
	onChangeimage(e) {
		this.setState({
			image: e.target.value,
		});
	}

	onSubmit(e) {
		e.preventDefault();
		const obj = {
			vName: this.state.vName,
			lName: this.state.lName,
			dob: this.state.dob,
			pNumber: this.state.pNumber,
			uName: this.state.uName,
			password: this.state.password,
			email: this.state.email,
			image: this.state.image,
		};

		// if(this.state.cNumber.length > 4){

		axios.post("http://localhost:4000/Products/add", obj).then((res) => {
			alert("add Successfully");
			this.setState({
				vName: "",
				lName: "",
				dob: "",
				pNumber: "",
				uName: "",
				password: "",
				email: "",
				image: "",
			});
			console.log(res.data);
		});
		this.props.history.push("/");

		window.location.replace("/");

		// }
		// else {
		//     alert('Pleace enter more than 4 digit.');
		// }
	}

	render() {
		return (
			<div className='EditProductsPage'>
				<div className='left-sidebar'>
					<img src={logo} alt='' className='header-logo' />
					<a href='/dashboard'>
						<div className='icon dashboard'>
							<img src={img1} alt='icon1' />
							<div className='text'>Dashboard</div>
						</div>
					</a>
					<a href='/farmer'>
						<div className='icon farmer'>
							<img src={img2} alt='icon2' />
							<div className='text'>Farmer</div>
						</div>
					</a>
					<a href='/vendor'>
						<div className='icon vendor'>
							<img src={img3} alt='icon3' />
							<div className='text'>Vendor</div>
						</div>
					</a>
					<a href='/product'>
						<div className='icon products'>
							<img src={img4} alt='icon4' />
							<div className='text'>Products</div>
						</div>
					</a>
					<a href='/clients'>
						<div className='icon clients'>
							<img src={img5} alt='icon5' />
							<div className='text'>Clients</div>
						</div>
					</a>
				</div>
				<div className='right-side'>
					<Header />

					<div className='EditProducts'>
						<table>
							<h2>Edit Products</h2>
							<tr className='left'>
								<td>
									<div className='basic-details'>
										<label for='first-name'>Product Name</label>
										<input
											type='text'
											id='first-name'
											name='first_name'
											required
											value={this.state.vName}
											onChange={this.onChangevName}
										/>
									</div>
								</td>
							</tr>
							<tr>
								<td>
									<div className='basic-details'>
										<label for='last-name'>Store Location</label>
										<input
											type='text'
											id='last-name'
											name='last_name'
											required
											value={this.state.lName}
											onChange={this.onChangelName}
										/>
									</div>
								</td>
							</tr>
							<tr>
								<td>
									<div className='basic-details'>
										<label for='username'>Username </label>
										<input
											type='text'
											id='username'
											name='username'
											required
											value={this.state.uName}
											onChange={this.onChangeuName}
										/>
									</div>
								</td>
							</tr>
							<tr>
								<td>
									<div className='basic-details'>
										<label for='customers'>Customers</label>
										<select name='' id=''>
											<option value="">Select Name</option>
											<option value='Name1'>Name1</option>
											<option value='Name2'>Name2</option>
										</select>
									</div>
								</td>
							</tr>
							<tr>
								<td>
									<div className='right'>
										<div className='basic-details'>
											<label htmlFor='fileInput'>
												<p>Add Image</p>
												<img src={draganddropimage} alt='imageicon' />
											</label>
											<input
												type='file'
												id='fileInput'
												value={this.state.image}
												onChange={this.onChangeimage}
											/>
										</div>
									</div>
								</td>
							</tr>
							<tr>
								<td>
									<button type='submit'>Save</button>
								</td>
							</tr>
						</table>
					</div>
				</div>
			</div>
		);
	}
}
