import  React, {Component} from 'react';
import axios from 'axios';
import {BrowserRouter as Router, Link} from "react-router-dom";
import {Button, Form, FormControl, Navbar} from "react-bootstrap";

import logo from "../logo.png";
import './css/LandingPage.css';
import Footer from './footer';


export default  class Create extends  Component{


    constructor(props) {
        super(props);
        this.onChangeName = this.onChangeName.bind(this);
        this.onChangeAddress = this.onChangeAddress.bind(this);
        this.onChangeNIC = this.onChangeNIC.bind(this);
        this.onChangePhone = this.onChangePhone.bind(this);
        this.onChangeEmail = this.onChangeEmail.bind(this);

        this.onSubmit = this.onSubmit.bind(this);

        this.state = {
            name: '',
            address: '',
            nic:'',
            phone:'',
            email:'',
            password:''

        }
    }
    onChangeName(e){
        this.setState( {
           name: e.target.value
        });
    }
    onChangeAddress(e){
        this.setState( {
            address: e.target.value
        });
    }
    onChangeNIC(e){
        this.setState( {
            nic: e.target.value,
            campusid:""+ e.target.value.substring(0, 6)

        });
    }
    onChangePhone(e){
        this.setState( {
            phone: e.target.value
        });
    }
    onChangeEmail(e){

            this.setState({
                email: e.target.value
            });
    }
    onChangePassword(e){
        this.setState( {
            password: e.target.value
        });
    }
    onChangecPassword(e){
        this.setState( {
            cpassword: e.target.value
        });
    }
    onSubmit(e){
        e.preventDefault();
        const obj = {
            name : this.state.name,
            address : this.state.address,
            nic : this.state.nic,
            phone : this.state.phone,
            email : this.state.email,
            password : this.state.password,
            campusid : this.state.campusid
        };

        const lastelement = this.state.nic.charAt(this.state.nic.length - 1);

        alert("Your Campus Id is - " +this.state.campusid);
        if(this.state.password ===  this.state.cpassword){
            if(this.state.password.length >= 8){
                if(this.state.phone.length === 10){
                    if(this.state.nic.length === 10){
                        if(lastelement === 'V' || lastelement === 'v'){
                            axios.post('http://localhost:4000/campus/add',obj)
                                .then(res => {
                                    alert("Registration Successfully");
                                    this.setState({
                                        name :'',
                                        address :'',
                                        nic :'',
                                        phone:'',
                                        email:'',
                                        password:'',
                                        cpassword:''

                                    })
                                    console.log(res.data)});
                            this.props.history.push('/signIn');
                        } 
                        else {
                            alert('Invalid NIC Number.. Pleace enter "V" for nic.');
                        }
                    } 
                    else {
                        alert('Invalid NIC Number.. Pleace enter 10 digits for nic.');
                    }
                }
                else{
                    alert('Invalid phone number.. Pleace enter 10 numbers for contact number.');
                }
            } 
            else {
                alert('Pleace enter at least 8 characters for passwords.');
            }
        }else{
            alert('Your password and confirm password are miss match... Pleace enter same passwords');
        }
    }




    render() {
        return(
            <div class = "wrap">
                    <Navbar>
                      </Navbar>

                      <nav className="navbar navbar-expand-lg navbar-light bg-info ">
                        <div className="collapse navbar-collapse" id = "navbarSupportedContent">
                            <ul className="navbar-nav mr-auto font-weight-bold form-control-lg text-dark ">

                                <li className="nav-item">
                                    <Link to={'/'} className = "nav-link">Home</Link>
                                </li>
                                <li className="nav-item">
                                    <Link to={'/create'} className = "nav-link ">Address Inventory</Link>
                                </li>

                                <li className="nav-item">
                                    <Link to={'/aboutUs'} className = "nav-link">About Us</Link>
                                </li>
                                <li className="nav-item">
                                    <Link to={'/aboutUs'} className = "nav-link">Contact Us</Link>
                                </li>
                                <li className="nav-item">
                                    <Link to={'/aboutUs'} className = "nav-link"></Link>
                                </li>
                                <li className="nav-item">
                                    <Link to={'/signIn'} className = "nav-link">Sign In</Link>
                                </li>

                            </ul>
                        </div>
                        <Form inline >
                        <div class="form-group">
                            <input class="form-control input-sm mr-1"  type="text" placeholder='search here....'/>
                            <Button type="submit" className='btn btn-info btn-sm'>search</Button>
                        </div>

                        </Form>
                    </nav>
                    <br/>
                    <div className='row-frm'>
                        <div className='col-frm-1'>
                            <img src = "https://turnkey.lk/wp-content/uploads/2013/12/b8.jpg" width = "" height = ""/>

                        </div>
                        <div className="container reg-frm" style={{marginTop:10}}>
                            <h3 className="text-center">Add New Inventory</h3>
                            <form onSubmit={this.onSubmit}>
                                <div className="form-group">
                                    <label>Product Name :</label>
                                    <input type ="text" required placeholder = "Please enter product  name" className="form-control" value={this.state.name} onChange = {this.onChangeName}/>
                                </div>

                                <div className="form-group">
                                    <label>Stock :</label>
                                    <input type ="text" required placeholder = "Please enter stock" className="form-control" value={this.state.nic} onChange = {this.onChangeNIC}/>
                                </div>
                                <div className="form-group">
                                    <label style={{color:'Black',fontWeight:'bold'}}>Product code :</label>
                                    <input type ="text"  className="form-control" value={this.state.campusid} readOnly style={{color:'green',fontWeight:'bold'}}/>
                                </div>
                                <div className="form-group">
                                    <label >Cost :</label>
                                    <input type ="text" required placeholder = "Please enter cost" className="form-control" value={this.state.phone} onChange = {this.onChangePhone}/>
                                </div>

                                <div className="form-group">
                                    <label>Vender :</label>
                                    <input type ="text" required placeholder = "Please enter product  name" className="form-control" value={this.state.name} onChange = {this.onChangeName}/>
                                </div>

                                <div className="form-group">
                                    <input type = "submit" value = "Register" className="btn btn-info"/>
                                </div>

                                <br/>

                            </form>
                        </div>
                    </div>

                    <br/><br/>

                    <div>
                         <hr className="shadow-lg card-footer"/>
                    </div>
                    <Footer/>
            </div>
        )
    }
}
